import './index.css';
import {Container, Row,Col} from 'react-bootstrap'
import FormInput from './Component/FormInput'
import QAlist from './Component/QAlist'
import React, {useState} from 'react'
import {questions} from './data'
const App = () => {

  const [data, setdata] = useState(questions);
  //to add new item
const addItem=()=>{

  setdata([...questions]); 

}
// to delete all data item 
const deleteAllItem=()=>{

  questions.splice(0,questions.length);
  setdata([]); 

}
  return (
  <div className="font text-center color-body">
  <Container className="p-5 ">
<Row className="justify-content-center">
  <Col sm="4">
<div className="fs-3 text-center my-2 ">اسئله واجوبه شائعه</div>
  </Col>
  <Col sm="8">
 <FormInput onAdd={addItem}/>
 <QAlist data={data}/>
 {
  data.length >= 1?(<button  onClick={deleteAllItem}
  className="btn-color w-100 my-2">مسح الكل </button>): null
 }
  </Col>
</Row>
</Container>

  </div>
  )
}

export default App
