import React,{useState} from 'react'
import { Row,Form,Col } from 'react-bootstrap'
import {questions} from './data'
const FormInput = () => {
    const [stateQues, setQues] = useState('')
    const [stateAnswer, setAnswer] = useState('')

    const addNewItem=(onAdd)=>{
        questions.push({})
        onAdd();
    }
  return (
   <Row className="my-3">
    
       <Col sm="5">
         <Form.Control onChange={(e)=>setQues(e.target.value)} type="email" placeholder="ادخل سؤال" />
       </Col>
       <Col sm="5">
         <Form.Control onChange={(e)=>setAnswer(e.target.value)} type="email" placeholder="ادخل الاجابه" />
       </Col>
      <Col sm="2">
      <button onclick={addNewItem} className="btn-color w-100" type="submit">
        اضافه
      </button>
      </Col>
   </Row>
  )
}

export default FormInput
